import React , { useState } from "react";
import clsx from "clsx";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";
import { Grid } from "@material-ui/core"
import { useStyles } from "./styles/style"
import { changerState } from "./globalState"

export function SideBar() {

  const [ value , setValue ] = useState('');
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const stayingState = event => {
    let { name } = event.target
    changerState({ value , name });
  }

//SIDEBAR
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };
  const takingValue = (e) => {
    setValue( ( e.target.value ) )
    handleDrawerClose()
  }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar className={ classes.appBar } >
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={handleDrawerOpen}
          edge="start"
          className={clsx(classes.menuButton, open && classes.hide)}
        >
          <MenuIcon />
        </IconButton>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="bottom"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div>
          <IconButton onClick={handleDrawerClose} className={ classes.x } >x</IconButton>
        </div>
        <TextareaAutosize className={ classes.textValue } onChange={takingValue}></TextareaAutosize>
        <Grid container className={ classes.btnCover } >
          <button name='a' className={ `${ classes.btnCoverbtn }` } onClick={ stayingState } > A </button>
          <button name='b' className={ `${ classes.btnCoverbtn }` } onClick={ stayingState } > B </button>
          <button name='c' className={ `${ classes.btnCoverbtn }` } onClick={ stayingState } > C </button>
          <button name='d' className={ `${ classes.btnCoverbtn }` } onClick={ stayingState } > D </button>
        </Grid>
      </Drawer>
        <div />
    </div>
  );
}
