import { makeStyles } from "@material-ui/core/styles";
import { useTheme } from "@material-ui/core/styles";

const drawerWidth = 240;

export const useStyles = makeStyles({
  cover: {
    width: "100%",
    height: "100vh",
  },
  first: {
    width: "100%",
    height: "50%",
    backgroundColor: "orange",
  },
  second: {
    width: "100%",
    height: "50%",
    backgroundColor: "orange",
  },
  firstGrid: {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    alignItems: "center",
    justifyContent: "space-around",
    width: "100%",
    height: "100%",
    backgroundColor: "green",
  },
  secondGrid: {
    width: "100%",
    height: "100%",
    backgroundColor: "blue",
  },
  buttonlar: {
    width: "20%",
    height: "25%",
    fontSize: "2.4em",
    color: "white",
    borderRadius: "8px",
    border: "none",
    userSelect: "none",
    outline: "none",
    transition: ".3s",
    boxShadow: " 0 0 28px -12px rgba( 0 , 0 , 0 , .5 ) ",
    background: "linear-gradient( to right , #4169E1 , #1E90FF )",
    "&:hover": {
      color: "white",
      transform: "scale( 1.023 )",
    },
    "&:active": {
      color: "black",
      boxShadow: " 0 0 28px -12px rgba( 0 , 0 , 0 , .7 )",
      transform: "scale( .95 )",
      background: "linear-gradient( to right , #1E90FF , #4169E1 )",
    },
  },
  item: {
    width: "100%",
    height: "100%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-around",
    alignItems: "center",
  },
  work: {
    width: "60%",
    backgroundColor: "white",
    padding: "3px",
    color: "red",
    borderRadius: "4px",
    boxShadow: "0 0 20px -10px rgba( 0 , 0 , 0 , .5 )",
  },
  //SideBar
  root: {
    display: "flex",
  },
  appBar: {
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(["margin", "width"], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: "flex",
    alignItems: "center",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: "flex-end",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create("margin", {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
});
