import { makeStyles } from '@material-ui/core/styles'

export const useStyles = makeStyles({
  main: {
    backgroundColor: "lightgrey",
    height: "100vh"
  },
  card:{
    height: "50vh",
    padding: "10px",
  },
  cont:{
    width: "100%",
    height: "100%",
    boxShadow:'0 0 10px -8px rgba( 0 , 0 , 0 , .4 )',
    backgroundColor:'red'
  },

})
