import { makeStyles } from '@material-ui/core/styles'

export const useStyles = makeStyles({
  main: {
    height: "100vh"
  },
  card:{
    height: "50vh",
    padding: "25px",
  },
  cont:{
    display:"flex",
    flexDirection:"row",
    flexWrap:'wrap',
    width: "100%",
    height: "100%",
    boxShadow:'0 0 20px -8px rgba( 0 , 0 , 0 , .4 )',
    borderRadius:'10px',
    padding:'15px',
    transition:'.5s',
    '&:hover':{
      transform:'scale( 1.025 )',
      boxShadow:'0 0 25px -12px rgba( 0 , 0 , 0 , .7 )',
    }
  },
  thing:{
    width:"20%",
    height:"40px",
    borderRadius:"2px",
    boxShadow:"0 0 12px -12px rgba( 0 , 255 , 0 , .5 )",
  }

})
