import { makeStyles } from '@material-ui/core/styles'

export const useStyles = makeStyles({
  cover:{
    width:'100%',
    height:'100vh',
  },
  first:{
    width:'100%',
    height:'50%',
    backgroundColor:'orange',
  },
  second:{
    width:'100%',
    height:'50%',
    backgroundColor:'orange',
  },
  firstGrid:{
    display:"flex",
    flexDirection:"row",
    flexWrap:"wrap",
    alignItems:"center",
    justifyContent:"space-around",
    width:"100%",
    height:"100%",
    backgroundColor:"green" 
  },
  buttonlar:{
    width:"20%",
    height:"25%",
    color:"white",
    borderRadius:"4px",
    outline:"none",
    userSelect:"none",
    transition:".3s",
    boxShadow:" 0 0 28px -12px rgba( 0 , 0 , 0 , .5 ) ",
    background:"linear-gradient( to right , #4169E1 , #1E90FF )",
    '&:hover':{
      transform:"scale( 1.023 )",
    },
    '&:active':{
      boxShadow:" 0 0 28px -12px rgba( 0 , 0 , 0 , .7 )",
      transform:"scale( .95 )",
      background:"linear-gradient( to right , #1E90FF , #4169E1 )",
    }
  }

})
