import { makeStyles } from '@material-ui/core/styles'
import { changerState , globalState } from '../globalState'

let {image} = globalState

export const useStyles = makeStyles({
  main: {
    height: "100vh"
  },
  card:{
    height: "50vh",
    padding: "25px",
  },
  cont:{
    display:"flex",
    flexDirection:"row",
    flexWrap:'wrap',
    backgroundImage: image.img_1 ,
    backgroundRepeat:"no-repeat",
    backgroundSize:"cover",
    width: "100%",
    height: "100%",
    boxShadow:'0 0 20px -8px rgba( 0 , 0 , 0 , .4 )',
    borderRadius:'10px',
    padding:'15px',
    transition:'.5s',
    '&:hover':{
      transform:'scale( 1.025 )',
      boxShadow:'0 0 25px -12px rgba( 0 , 0 , 0 , .7 )',
    }
  },
  thing:{
    margin:'7.5x',
    backgroundColor:"white",
    width:"20%",
    height:"40px",
    borderRadius:"2px",
    boxShadow:"0 0 22px -8px rgba( 0 , 0 , 0 , .8 )",
  }

})
