import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";
import { Button } from "@material-ui/core";
import { Grid } from "@material-ui/core"

const drawerHeight = "20%";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  hide: {
    display: "none",
  },
  drawer: {
    height: drawerHeight,
    flexShrink: 0,
  },
  drawerPaper: {
    width: "15%",
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    marginTop: -drawerHeight,
  },
  contentShift: {
    marginTop: 0,
  },
  btnCover:{
    width:"100%",
    display:"flex",
    flexDirection:"row",
    flexWrap:"no-wrap",
    justifyContent:"center",
    alignItems:'space-around',
  },
  btnCoverbtn:{
    width:"25%",
    color:"white",
  }
}));

export function SideBar() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const textValue = (e) => {
    console.log(e.target.value);
  };

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
      >
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={handleDrawerOpen}
          edge="start"
          className={clsx(classes.menuButton, open && classes.hide)}
        >
          <MenuIcon />
        </IconButton>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div>
          <IconButton onClick={handleDrawerClose}>x</IconButton>
        </div>
        <TextareaAutosize
          className={`${classes.textValue}`}
          onChange={textValue}
        ></TextareaAutosize>
        <Grid container className={ classes.btnCover } >
          <Button className={ `${ classes.btnCoverbtn } btn-danger ` } > A </Button>
          <Button className={ `${ classes.btnCoverbtn } btn-primary ` } > B </Button>
          <Button className={ `${ classes.btnCoverbtn } btn-success ` } > C </Button>
          <Button className={ `${ classes.btnCoverbtn } btn-secondary ` } > D </Button>
        </Grid>
      </Drawer>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div />
      </main>
    </div>
  );
}
