import { makeStyles } from '@material-ui/core/styles'
import { changerState , globalState } from '../globalState'

export const useStyles = makeStyles({
  main: {
    height: "100vh"
  },
  card:{
    height: "50vh",
    padding: "25px",
  },
  thing:{
    margin:'9.2px',
    backgroundColor:"white",
    width:"100%",
    height:"40px",
    borderRadius:"2px",
    boxShadow:"0 0 22px -8px rgba( 0 , 0 , 0 , .8 )",
  }

})
