import React , { useState } from "react";
import clsx from "clsx";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";
import { Button } from "@material-ui/core";
import { Grid } from "@material-ui/core"
import { useStyles } from "./styles/style"

export function SideBar() {

  const [ value , setValue ] = useState('')
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const takingValue = (e) => {
    setValue( ( e.target.value ) )
  }

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar className={ classes.appBar } >
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={handleDrawerOpen}
          edge="start"
          className={clsx(classes.menuButton, open && classes.hide)}
        >
          <MenuIcon />
        </IconButton>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div>
          <IconButton onClick={handleDrawerClose} className={ classes.x } >x</IconButton>
        </div>
        <TextareaAutosize
          placeholder="type something..."
          className={`${classes.textValue}`}
          onChange={takingValue}
        ></TextareaAutosize>
        <Grid container className={ classes.btnCover } >
          <Button className={ `${ classes.btnCoverbtn }` } onClick={ () => console.log( value )} > A </Button>
          <Button className={ `${ classes.btnCoverbtn }` } > B </Button>
          <Button className={ `${ classes.btnCoverbtn }` } > C </Button>
          <Button className={ `${ classes.btnCoverbtn }` } > D </Button>
        </Grid>
      </Drawer>
        <div />
    </div>
  );
}
