import { makeStyles } from '@material-ui/core/styles'
import { changerState , globalState } from '../globalState'

export const useStyles = makeStyles({
  main: {
    height: "100vh"
  },
  card:{
    height: "50vh",
    padding: "25px",
  },
  cont:{
    display:"flex",
    flexDirection:"row",
    flexWrap:'wrap',
    backgroundColor:"#8E44AD",
    fontFamily:"'Indie Flower', cursive;",
    color:"white",
    fontSize:"30em",
    width: "100%",
    height: "100%",
    boxShadow:'0 0 20px -8px rgba( 0 , 0 , 0 , .4 )',
    borderRadius:'10px',
    padding:'15px',
    transition:'.5s',
    '&:hover':{
      transform:'scale( 1.025 )',
      boxShadow:'0 0 25px -12px rgba( 0 , 0 , 0 , .7 )',
      backgroundColor:"#6C3483",
    }
  },
  thing:{
    margin:'9.2px',
    backgroundColor:"white",
    width:"20%",
    height:"40px",
    borderRadius:"2px",
    boxShadow:"0 0 22px -8px rgba( 0 , 0 , 0 , .8 )",
  }

})
