import { makeStyles } from '@material-ui/core/styles'
import { changerState , globalState } from '../globalState'

export const useStyles = makeStyles({
  main: {
    height: "100vh"
  },
  card:{
    height: "50vh",
    padding: "25px",
  },
  cont:{
    display:"flex",
    flexDirection:"row",
    justifyContent:"center",
    alignItems:"center",
    flexWrap:'wrap',
    backgroundColor:"rgba( 0 , 0 , 0 , .3 )",
    fontFamily:"'Special Elite', cursive",
    color:"rgba( 255 , 255 , 255 , .7 )",
    userSelect:"none",
    fontSize:"9em",
    width: "100%",
    height: "100%",
    boxShadow:'0 0 36px -10px rgba( 0 , 0 , 0 , .7 )',
    borderRadius:'10px',
    padding:'15px',
    transition:'.5s',
    '&:hover':{
      transform:'scale( 1.025 )',
      boxShadow:'0 0 25px -12px rgba( 0 , 0 , 0 , .7 )',
      backgroundColor:"rgba( 0 , 0 , 0 , 0.5 )",
      color:"white"
    },
    '&:active':{
      transform:"scale( 0.9 ) rotate( 10deg ) "
    }
  },
  thing:{
    margin:'9.2px',
    backgroundColor:"white",
    width:"20%",
    height:"40px",
    borderRadius:"2px",
    boxShadow:"0 0 22px -8px rgba( 0 , 0 , 0 , .8 )",
  }

})
