import { makeStyles } from '@material-ui/core/styles'

export const useStyles = makeStyles({
  cover:{
    width:'100%',
    height:'100vh',
  },
  first:{
    width:'100%',
    height:'50%',
    backgroundColor:'orange',
  },
  second:{
    width:'100%',
    height:'50%',
    backgroundColor:'orange',
  },
  firstGrid:{
    display:"flex",
    flexDirection:"row",
    flexWrap:"wrap",
    alignItems:"center",
    justifyContent:"space-around",
    width:"100%",
    height:"100%",
    backgroundColor:"green"
    
  }

})
