import { observable, action } from "mobx";

const _globalState = {
  a: [],
  b: [],
  c: [],
  d: [],
};
export let globalState = observable(_globalState);

let _changerState = (props) => {
  let { value, name } = props;

  globalState = {
    ...globalState,
  };

  globalState[name].push( value )

};

export let changerState = action(_changerState);
