import React from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import TextareaAutosize from "@material-ui/core/TextareaAutosize";
import { Button } from "@material-ui/core";
import { Grid } from "@material-ui/core"
import { useStyles } from "./styles/style"

export function SideBar() {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const textValue = (e) => {
    console.log(e.target.value);
  };

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar
        className={clsx(classes.appBar, {
          [classes.appBarShift]: open,
        })}
      >
        <IconButton
          color="inherit"
          aria-label="open drawer"
          onClick={handleDrawerOpen}
          edge="start"
          className={clsx(classes.menuButton, open && classes.hide)}
        >
          <MenuIcon />
        </IconButton>
      </AppBar>
      <Drawer
        className={classes.drawer}
        variant="persistent"
        anchor="left"
        open={open}
        classes={{
          paper: classes.drawerPaper,
        }}
      >
        <div>
          <IconButton onClick={handleDrawerClose}>x</IconButton>
        </div>
        <TextareaAutosize
          className={`${classes.textValue}`}
          onChange={textValue}
        ></TextareaAutosize>
        <Grid container className={ classes.btnCover } >
          <Button className={ `${ classes.btnCoverbtn } ` } color={ danger } > A </Button>
          <Button className={ `${ classes.btnCoverbtn } ` } color={ primary } > B </Button>
          <Button className={ `${ classes.btnCoverbtn } ` } color={ success } > C </Button>
          <Button className={ `${ classes.btnCoverbtn } ` } color={ secondary } > D </Button>
        </Grid>
      </Drawer>
      <main
        className={clsx(classes.content, {
          [classes.contentShift]: open,
        })}
      >
        <div />
      </main>
    </div>
  );
}
