import React , { useState } from 'react';

//COMPONENTS
import { SignIn } from './SignIn.jsx'
import { SignUp } from './SignUp.jsx'

export function Form() {

  const [ isUser , setUser ] = useState( true )

  setUser = () => {isUser = !isUser }

  return (
    <div>
      { isUser ? <SignIn /> : <SignUp /> }
    </div>
  )
}
