import React , { Component } from 'react'

export class Form extends Component{
  
  constructor( props ){
    super( props )
    this.state = ({
      username:'',
      nickname:'',
      email:'',
      password:'',
    })
  }

  render(){

    let { username , nickname , email , password } = this.state;

    let handlerChange = (event) => {
      
      event.preventDefault()

      let { name , value } = event.target
      this.setState({ [name]: value })
      console.log( this.state )
    }

    return(
      <form onSubmit={ handlerChange } autoComplete='off' >
        <p>
          <label> Full-name: </label>
          <input type="text" name='username' onChange={ handlerChange }  value={ username } placeholder='Full name' />
        </p>
        <p>
          <label> Nick-name: </label>
          <input type="text" name='nickname' onChange={ handlerChange }  value={ nickname } placeholder='Nick name' />
        </p>
        <p>
          <label> Email address: </label>
          <input type="text" name='email' onChange={ handlerChange }  value={ email } placeholder='Email' />
        </p>
        <p>
          <label> Password: </label>
          <input type="password" name='password' onChange={ handlerChange }  value={ password } placeholder='Password' />
        </p>
          <button type="submit">Submit</button>
          <button type="reset">Reset</button>
      </form>
    )
  }
}
