import React , { Component } from 'react'

let initialValues = { username:'' , nickname:'' , email: '' , password:'' }
export class Form extends Component{
  
  constructor( props ){
    super( props )
    this.state = initialValues
  }

  render(){

    let { username , nickname , email , password } = this.state;

    let handlerChange = (event) => {
      let { name , value } = event.target
      this.setState({ [name]: value })
      console.log( this.state )
    }

    let handlerReset = () => this.setState( initialValues )

    let handlerSubmitted = e => e.preventDefault()

    return(
      <form onSubmit={ handlerSubmitted } autoComplete='off' >
        <p>
          <label> Full-name: </label>
          <input
            type="text"
            name='username'
            onChange={ handlerChange }
            value={ username }
            placeholder='Full name'
          />
          <h3>{ username }</h3>
        </p>
        <p>
          <label> Nick-name: </label>
          <input
            type="text"
            name='nickname'
            onChange={ handlerChange }
            value={ nickname }
            placeholder='Nick name'
          />
          <h3>{ nickname }</h3>
        </p>
        <p>
          <label> Email address: </label>
          <input
            type="text"
            name='email'
            onChange={ handlerChange }
            value={ email }
            placeholder='Email'
          />
          <h3>{ email }</h3>
        </p>
        <p>
          <label> Password: </label>
          <input
            type="password"
            name='password'
            onChange={ handlerChange }
            value={ password }
            placeholder='Password'
          />
          <h3>{ password }</h3>
        </p>
          <button type="submit">Submit</button>
          <button type="reset" onClick={ handlerReset } >Reset</button>
      </form>
    )
  }
}
