import React from "react";
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import { useStyles } from "./styles/style";
//material-ui
import { Grid , Container } from "@material-ui/core";

import { NavLink, Route, Switch, BrowserRouter as Bro } from "react-router-dom";

export const Main = () => {
  let classes = useStyles();

  return (
    <Bro>
      <div className="main">
        <React.Fragment>
          <Container justify="center" maxWidth="md">
            
            <Grid container className={`${classes.cover} p-md-3 p-3 bg-secondary`} md={12} xs={12}>
              <Grid item md={12} xs={12} className={`p-md-3 p-3 bg-primary ${ classes.first }`} >
                <Grid class={classes.firstGrid} item xs={ 12 } md={ 12 } >
                  <NavLink type={ 'NavLink' } to={'/a'} activeClassName={ `btn ${classes.NavLink}` } >A</NavLink>
                  <NavLink type={ 'NavLink' } to={'/b'} activeClassName={ `btn ${classes.NavLink}` } >B</NavLink>
                  <NavLink type={ 'NavLink' } to={'/c'} activeClassName={ `btn ${classes.NavLink}` } >C</NavLink>
                  <NavLink type={ 'NavLink' } to={'/d'} activeClassName={ `btn ${classes.NavLink}` } >D</NavLink>
                </Grid>
              </Grid>
              <Grid item md={12} xs={12} className={`p-md-3 p-3 bg-danger ${ classes.second } `} >
                <Switch>
                  <Route path="/a" component={A} />
                  <Route path="/b" component={B} />
                  <Route path="/c" component={C} />
                  <Route path="/d" component={D} />
                </Switch>
              </Grid>
            </Grid>
          </Container>
        </React.Fragment>
      </div>
    </Bro>
  );
};
