import { observable, action } from "mobx";

const _globalState = [[], [], [], []];
export let globalState = observable(_globalState);

let _changerState = props => {
  let { value, name } = props;

  if (name === "a") {
    globalState[0].push(value);
  } else if (name === "b") {
    globalState[1].push(value);
  } else if (name === "c") {
    globalState[2].push(value);
  } else {
    globalState[3].push(value);
  }

};

export let changerState = action(_changerState);


let _deleterStateItem = props => {

  let k = props.split("")
  let n = k.length-1
  console.log( props )

  if( k[n] === 'a' ){
    globalState[0] = globalState[0].filter( ( item, index ) => index !== props )
  } else if( k[n] === 'b' ){
    globalState[1] = globalState[1].filter( ( item, index ) => index !== props )
  } else if( k[n] === 'c' ){
    globalState[2] = globalState[2].filter( ( item, index ) => index !== props )
  } else {
    globalState[3] = globalState[3].filter( ( item, index ) => index !== props )
  }

}

export const deleterStateItem = action( _deleterStateItem )
